    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String peso = jTextField1.getText();
        String altura = jTextField2.getText();
        double pesoINT = Double.parseDouble(peso);
        double alturaINT = Double.parseDouble(altura);

        double imc = (pesoINT / (alturaINT*alturaINT));
        if (imc < 18.5) {
            jTextField3.setText("Peso inferior al normal. Tu IMC es: " + String.format("%.2f", imc));
           
       }else if (imc >= 18.5 && imc <=24.9) {
            jTextField3.setText("Normal. Tu IMC es: " + String.format("%.2f", imc));

        }else if (imc >= 25.0 && imc <= 29.9) {
            jTextField3.setText("Peso superior al normal. Tu IMC es: " + String.format("%.2f", imc));
           
        }else{
             jTextField3.setText("Obesidad. Tu IMC es: " + String.format("%.2f", imc));

        }

    }//GEN-LAST:event_jButton1ActionPerformed

